// Add ripple effect to buttons
document.addEventListener('DOMContentLoaded', function() {
  // Add ripple class to all buttons
  const buttons = document.querySelectorAll('.btn');
  buttons.forEach(function(button) {
    button.classList.add('ripple');
  });

  // Add shadow classes to cards
  const cards = document.querySelectorAll('.card');
  cards.forEach(function(card) {
    card.classList.add('z-depth-1');
    
    // Add hover effect
    card.addEventListener('mouseenter', function() {
      this.classList.remove('z-depth-1');
      this.classList.add('z-depth-2');
    });
    
    card.addEventListener('mouseleave', function() {
      this.classList.remove('z-depth-2');
      this.classList.add('z-depth-1');
    });
  });

  // Add shadow to navbar
  const navbar = document.querySelector('.navbar');
  if (navbar) {
    navbar.classList.add('z-depth-1');
  }
});
